    return Object.entries(Object.getOwnPropertyDescriptors(obj))
      .filter(([, descriptor]) => descriptor.enumerable)
      .map(([key]) => key);
  }

  static forEachSafe<T extends object>(obj: T, callback: (value: any, key: string) => void) {
    for (const key of this.getSafeKeys(obj)) {
      callback((obj as any)[key], key);
    }
  }

  static createSafeMap<K, V>(): Map<K, V> {
    // 无原型链污染的安全字典
