const trackPromise = (promise: Promise<T>) => {
  const state = { status: "pending", value: null };
  const proxy = new Proxy(promise, {
    get(_, prop) {
      if (prop === "status") return state.status;
      if (prop === "value") return state.value;
      return Reflect.get(promise, prop);
    },
  });
  promise.then(res => { state.status = "fulfilled"; state.value = res; })
         .catch(() => { state.status = "rejected"; });
  return proxy;
};

